INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(1, 'Nature', 'Sala que estimula los sentidos en un ambiente completamente natural, cielo de colores, espacio verde, ideal para toma de aromaterapia y masaje con esencias naturales', '', '', 1, 1);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(2, 'Harmony', 'Sala dedicada a restaurar la armonía cuerpo-mente, cielo de bosque, espacio boscoso, ideal para tratamientos de Peeling corporal suave, Batido natural a escoger y Masaje con aceites esenciales', '', '', 1, 1);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(3, 'True Rest', 'Sala que favorece el relajamiento y liberación de tensiones, cielo estrellado, espacio galaxia, ideal para la toma de todos los tipos de masajes.', '', '', 1, 1);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(4, 'Aqua', 'Sala dedicada a los amantes de ambientes marinos, cielo natural, espacio marino, ideal para tratamientos de Hidroterapia, Bañera de hidromasaje, Ducha de sensaciones, Masaje Hawaiano', '', '', 1, 1);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(5, 'Single', 'Sala que estimula la concentración, cielo natural, espacio adaptable (color a elegir), ideal para tratamientos de masajes y exfoliaciones. ', '', '', 1, 2);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(6, 'Balance', 'Sala que balancea los sentidos en un ambiente completamente natural, cielo y espacio verde, ideal para toma de aromaterapia y masaje con esencias naturales', '', '', 1, 2);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(7, 'Meditation', 'Sala dedicada a restaurar la armonía mente, cielo de bosque, espacio boscoso, ideal para tratamientos de Peeling corporal suave, Aromaterapia y Masaje con aceites esenciales', '', '', 1, 2);
INSERT INTO sala (idSala, nombre, descripcion, foto, rutaFoto, estatus, idSucursal) values(8, 'Advance', 'Sala que te hará sentir especial, cielo al natural, ambiente persa, ideal para que se pueda tomar cualquiera de los tratamientos disponibles. ', '', '', 1, 3);
