INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (1, 'Aromaterapia', 'Descansa y relájate en un ambiente especial, mientras inhalas esencias naturales que te ayudaran a mejorar tu estado emocional. ', 315);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (2, 'Batido natural', 'La leche tiene propiedades que producen colágeno, que reafirman y hacen que la piel sea más flexible. Este tratamiento consiste en un masaje general (facial y corporal) basado en cremas y leches naturales.', 320);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (3, 'Ducha de sensaciones', 'Es un masaje energético que combina masaje corporal, facial, del cuero cabelludo, siempre al ritmo de la música, con uso de esencias de aromaterapia finalizando con hidromasaje.', 252);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (4, 'Exfoliación corporal', 'Es un tratamiento específico para la eliminación de las células muertas de la piel mediante el uso de exfoliantes y emolientes naturales.', 301);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (5, 'Exfoliación facial', 'Es un tratamiento específico para la eliminación de las células muertas de la piel facial mediante el uso de exfoliantes y emolientes naturales.', 213);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (6, 'Hidromasaje', 'El tratamientos de hidromasaje esta concebido para ofrecer la relajación y el placer del agua sobre todo el cuerpo junto con los beneficios de la cromoterapia.', 159);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (7, 'Hidroterapia', 'El tratamientos de hidroterapia esta concebido para ofrecer la relajación y el placer del agua sobre todo el cuerpo, primero se proporciona un masaje corporal, para después pasar a la tina de hidromasaje junto con los beneficios de la cromoterapia, y aromaterapia.', 362);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (8, 'Masaje con aceites esenciales', 'Disfruta de un rato inolvidable con el masaje de aceites esenciales, podrás elegir los aceites a tu gusto con los que recibirás un delicado y relajante masaje general (facial y corporal).', 184);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (9, 'Masaje con esencias naturales', 'Disfruta de un rato inolvidable con la micro exfoliación y el masaje de esencias naturales podrás elegir los componentes a tu gusto con los que recibirás un delicado y relajante masaje general (facial y corporal).', 241);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (10, 'Masaje Hawaiano', 'Masaje terapéutico acompañado de la aplicación de frutos naturales como base del masaje.', 194);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (11, 'Peeling corporal intenso', 'El peeling corporal es un tratamiento específico para la eliminación de las células muertas de la piel con una sesión de exfoliación, seguida por el peeling , aportando frescura, suavidad y limpieza a la piel.', 298);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (12, 'Peeling corporal suave', 'El peeling facial es un tratamiento específico para la eliminación de las células muertas de la piel aportando frescura, suavidad y limpieza a la piel.', 265);
INSERT INTO tratamiento (idTratamiento, nombre, descripcion, costo) VALUES (13, 'Especial', 'Solicita libremente la combinación de tratamientos que necesites, de manera personalizada.', 318);
